import React, {useState, useEffect, useContext} from "react";
import { Link, navigate } from "@reach/router";



const SearchBlock = () => {
    const [blockTag, setBlockTag] = useState("")

    function handleBlockTag(e) {
        e.preventDefault();
    }

    function onChangeBlockTag(e) {
        setBlockTag(e.target.value);
    }

    return(
        <div className="search-block">
            <form onSubmit={(e) => {handleBlockTag(e);}}>
                <label>
                    Block visina/hash
                </label>
                <input
                    type="text"
                    placeholder="Enter block hash/size"
                    value={blockTag}
                    onChange={onChangeBlockTag}
                    onBlur={onChangeBlockTag}
            ></input>
            <button onClick={() => navigate()}>BlockTag</button>
            </form>
        </div>
    );
}

export default SearchBlock;
