import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";


const NewTransactionDetail = () => {
    const params = useParams()
    const [transaction, setTransaction] = useState([]);
    const [fee, setFee] = useState([])
    const [nTx, setNtx] = useState("");
    const [nVin, setNvin] = useState("");
    const [nVout, setNvout] = useState("");

    useEffect(() => {
        fetch("http://localhost:6600/getTransaction/" + params.txId).then((response) => response.json())
            .then((result) => {
                console.log(result)
                setTransaction(result)
                setNtx(result.ntx);
                setNvin(result.vin.length);
                setNvout(result.vout.length);
                getFeea()
            })  
    }, [])

     function getFeea() {
        fetch("http://localhost:6600/getFee/" + params.txId).then((response) => response.json())
        .then((result) => {
            console.log(result)
            if(isNaN("a")) {
                setFee(result)
            }
            else {
                setFee([])
            }
        })
    }

    return(
        <div>
            <table className="table">
            <thead>
                <tr>
                <th scope="col">Transakcija</th>
                <th scope="col">{transaction.txid}</th>
                </tr>
            </thead>
            <tbody>
            <tr>
                <th scope="row">Naknada</th>
                {/* <td>{fee.length !== 0 ? (
                    <td>{fee}</td>
                ):(<></>)}</td> */}
                </tr>
                <tr>
                    <th scope="row">Verzija</th>
                    <td>{transaction.version}</td>
                </tr>
                <tr>
                    <th scope="row">Hash</th>
                    <td>{transaction.hash}</td>
                </tr>
                <tr>
                    <th scope="row">Velicina</th>
                    <td>{transaction.size} B</td>
                </tr>
                <tr>
                    <th scope="row">Virtualna velicina</th>
                    <td>{transaction.vsize} vB</td>
                </tr>
                <tr>
                    <th scope="row">Te≈æina</th>
                    <td>{transaction.weight}</td>
                </tr>
                <tr>
                    <th scope="row">Broj transakcija</th>
                    <td>{nVout}</td>
                </tr>
            </tbody>
            </table>
        </div>
    )
}

export default NewTransactionDetail;
