import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";


const NewTransactionDetail = () => {
    const params = useParams()
    const [transaction, setTransaction] = useState([]);
    const [fee, setFee] = useState([])

    useEffect(() => {
        console.log("AAAAAAA " + typeof params.txId)

        fetch("http://localhost:6600/getTransaction/" + params.txId).then((response) => response.json())
            .then((result) => {
                console.log(result)
                setTransaction(result)
                // getFee() 
            })  

        // const feea = await getFee();
        // setFee(feea)
    }, [])

     function getFee() {
        fetch("http://localhost:6600/getFee/" + params.txId.toString()).then((response) => response.json())
        .then((result) => {
            console.log(result)
            setFee(result)
        })
    }

    return(
        <div>
            <table className="table">
            <thead>
                <tr>
                <th scope="col">Transakcija</th>
                <th scope="col">{transaction.txid}</th>
                </tr>
            </thead>
            <tbody>
            <tr>
                <th scope="row">Naknada</th>
                <td>{fee}</td>
                </tr>
                <tr>
                    <th scope="row">Verzija</th>
                    <td>{transaction.version}</td>
                </tr>
                <tr>
                    <th scope="row">Hash</th>
                    <td>{transaction.hash}</td>
                </tr>
                <tr>
                    <th scope="row">Velicina</th>
                    <td>{transaction.size} B</td>
                </tr>
                <tr>
                    <th scope="row">Virtualna velicina</th>
                    <td>{transaction.vsize} vB</td>
                </tr>
                <tr>
                    <th scope="row">Te≈æina</th>
                    <td>{transaction.weight}</td>
                </tr>
                {/* <tr>
                    <th scope="row">Broj ulaza</th>
                    <td>{transaction.vin.length}</td>
                </tr> */}
                {/* <tr>
                    <th scope="row">Broj izlaza</th>
                    <td>{transaction.vout.length}</td>
                </tr> */}
            </tbody>
            </table>
        </div>
    )
}

export default NewTransactionDetail;
