import React, {useState, useEffect, useContext} from "react";


const SearchBlock = () => {
    const [blockTag, setBlockTag] = useState("");
    const [blockInfo, setBlockInfo] = useState([]);

    // useEffect(() => {
    //     fetch("http://localhost:6600/start").then((response) => response.json())
    //     .then((info) => {
    //         console.log(info)
    //     })
    // }, [])

    function handleBlockTag(e) {
        e.preventDefault();
    }

    function onChangeBlockTag(e) {
        nekaFunk(e.target.value) 
        setBlockTag(e.target.value);
    }

    async function nekaFunk(value) {
        if(value.substring(0, 11) == "00000000000") {
            fetch("http://localhost:6600/getBlock/" + value).then((response) => response.json())
            .then((result) => {

            })
        }

        else if(value.length == 6 || value.length == 7) {
            fetch("http://localhost:6600/blockInfo/" + value).then((response) => response.json())
            .then((info) => {
                setBlockInfo(info)
            })
        }
        else {
            fetch("http://localhost:6600/getTransaction/" + value).then((response) => response.json())
            .then((result) => {

            })
        }

        fetch("http://localhost:6600/start").then((response) => response.json())
        .then((info) => {
            console.log(typeof info);
            setBlockInfo(info);
            // setBlockTag(info)
        })
    }

    return(
        <div className="search-block">
            <form onSubmit={(e) => {handleBlockTag(e);}}>
                <label>
                   Pretraga po visini bloka, hash
                </label>
                <input
                    type="text"
                    value={blockTag}
                    onChange={onChangeBlockTag}
                    onBlur={onChangeBlockTag}
            ></input>
            <button >BlockTag</button>
            </form>
            <h1>{blockInfo.chain}</h1>
        </div>
    );
}

export default SearchBlock;
