import React, {useState, useEffect, useContext} from "react";
import { client } from "./Client";


const SearchBlock = () => {
    const [blockTag, setBlockTag] = useState("")

    function handleBlockTag(e) {
        e.preventDefault();
    }

    function onChangeBlockTag(e) {
        console.log("AAAAAAA tekst " + e.target.value);
        setBlockTag(e.target.value);
        nekaFunk()
    }

    async function nekaFunk() {
        client.getBlockchainInfo().then((help) => console.log(help));
    }

    return(
        <div className="search-block">
            <form onSubmit={(e) => {handleBlockTag(e);}}>
                <label>
                   Pretraga po visini bloka, hash
                </label>
                <input
                    type="text"
                    value={blockTag}
                    onChange={onChangeBlockTag}
                    onBlur={onChangeBlockTag}
            ></input>
            <button >BlockTag</button>
            </form>
        </div>
    );
}

export default SearchBlock;
